mapper.select:
SELECT c.documento AS c_documento, c.nombre AS c_nombre, c.telefono AS c_telefono, c.direccion AS c_direccion, c.email AS c_email, c.vetado AS c_vetado,
CASE WHEN ir.fechainiciorenta IS NOT NULL THEN strftime('%Y-%m-%d %H:%M:%f', ir.fechainiciorenta) ELSE NULL END AS ir_fechainiciorenta, CASE WHEN ir.fechafinrenta IS NOT NULL THEN strftime('%Y-%m-%d %H:%M:%f', ir.fechafinrenta) ELSE NULL END AS ir_fechafinrenta,
i.id AS i_id, i.nombre AS i_nombre, i.descripcion AS i_descripcion, strftime('%Y-%m-%d %H:%M:%f', i.fechalanzamiento) AS i_fechaLanzamiento, i.tarifaxdia AS i_tarifaxDia, i.formatorenta AS i_formatoRenta, i.genero AS i_genero, ti.id AS ti_id, ti.descripcion AS ti_descripcion FROM VI_CLIENTES c LEFT JOIN VI_ITEMRENTADO ir ON c.documento = ir.CLIENTES_documento LEFT JOIN VI_ITEMS i ON ir.ITEMS_id = i.id LEFT JOIN VI_TIPOITEM ti ON i.TIPOITEM_id = ti.id WHERE c.documento = #{id}
